INSERT INTO `mod_email_paterns_i18n` (`id`, `locale`, `theme`, `user_message`,  `admin_message`, `description`, `variables`) VALUES
(1, 'ru', 'Заказ товара', '<p><span>Здравствуйте, $userName$.</span><br /><br /><span>Мы благодарны Вам за то, что совершили заказ в нашем магазине "ImageCMS Shop"</span><br /><br /><span>Вы указали следующие контактные данные:</span><br /><br /><span>Email адрес: $userEmail$</span><br /><br /><span>Номер телефона: $userPhone$</span><br /><br /><span>Адрес доставки: $userDeliver$</span><br /><br /><span>Менеджеры нашего магазина вскоре свяжутся с Вами и помогут с оформлением и оплатой товара.</span><br /><br /><span>Также, Вы можете всегда посмотреть за статусом Вашего заказа, перейдя по ссылке:&nbsp; $orderLink$.</span><br /><br /><span>Спасибо за ваш заказ, искренне Ваши, сотрудники ImageCMS Shop.</span><br /><br /><span>При возникновении любых вопросов, обращайтесь за телефонами:</span><br /><br /><span>+7 (095) 222-33-22 +38 (098) 222-33-22</span></p>', '<p>Пользователь&nbsp;<span>$userName$ совершил заказ товара&nbsp;</span></p>\n<p><span><span>Email адрес: $userEmail$</span><br /><br /><span>Номер телефона: $userPhone$</span><br /><br /><span>Адрес доставки: $userDeliver$</span></span></p>', '<p><span>Уведомление покупателя о совершении заказа</span></p>', 'a:8:{s:10:"$userName$";s:31:"Имя пользователя";s:11:"$userEmail$";s:30:"Email Пользователя";s:11:"$userPhone$";s:39:"Телефон Пользователя";s:13:"$userDeliver$";s:27:"Адрес доставки";s:11:"$orderLink$";s:28:"Ссылка на заказ";s:10:"$products$";s:29:"Продукты заказа";s:11:"$checkLink$";s:37:"Ссылка на чек заказа";s:12:"$totalPrice$";s:23:"Сумма заказа";}'),
(2, 'ru', 'Смена статуса заказа', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Здравствуйте, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Статус вашего заказа изменен на <strong>$status$</strong></p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Вы указали следующие контактные данные:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Email адрес: </span>$userEmail$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Номер телефона: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Адрес доставки: </span>$userDeliver$</div>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Менеджеры нашего магазина вскоре свяжутся с Вами и помогут с оформлением и оплатой товара.</p>\n<p style="font-family: arial; font-size: 13px;">Также, Вы можете всегда посмотреть за статусом Вашего заказа, <a href="$orderLink$" target="_blank">перейдя по ссылке</a>.</p>', '', '<p>Смена статуса заказа</p>', 'a:6:{s:10:"$userName$";s:31:"Имя пользователя";s:11:"$userEmail$";s:30:"Email Пользователя";s:11:"$orderLink$";s:28:"Ссылка на заказ";s:8:"$status$";s:25:"Статус заказа";s:11:"$userPhone$";s:39:"Телефон пользователя";s:13:"$userDeliver$";s:27:"Адрес доставки";}'),
(3, 'ru', 'Товар появился на складе!', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Здравствуйте, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Товар&nbsp;<a href="$productLink$" target="_blank">$productName$</a>&nbsp;появился на складе. Вы можете его заказать.</p>', '', '<p>Уведомление о появлении</p>', 'a:5:{s:10:"$userName$";s:31:"Имя пользователя";s:11:"$userEmail$";s:30:"Email Пользователя";s:13:"$productName$";s:33:"Название продукта";s:8:"$status$";s:12:"Статус";s:13:"$productLink$";s:32:"Ссылка на продукт";}'),
(4, 'ru', 'Создание пользователя', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Здравствуйте, $user_name$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Поздравляем! Ваша регистрация прошла успешно.</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Данные для входа в магазин:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Email адрес: </span>$user_email$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Пароль: </span>$user_password$</div>', '<p><span>Создан пользователь $user_name$:</span><br /><span>С паролем: $user_password$</span><br /><span>Адресом: &nbsp;$<span>user_</span>address$</span><br /><span>Email пользователя: $user_email$</span><br /><span>Телефон пользователя: $user_phone$</span></p>', '<p>Шаблон письма на создание пользователя</p>', 'a:5:{s:11:"$user_name$";s:31:"Имя пользователя";s:15:"$user_password$";s:12:"Пароль";s:14:"$user_address$";s:12:"Адресс";s:12:"$user_email$";s:5:"Email";s:12:"$user_phone$";s:14:"Телефон";}'),
(5, 'ru', 'Восстановление пароля', '<p><span>Здравствуйте!</span><br /><br /><span>На сайте $webSiteName$ создан запрос на восстановление пароля для Вашего аккаунта.</span><br /><br /><span>Для завершения процедуры восстановления пароля перейдите по ссылке $resetPasswordUri$</span><br /><br /><span>Ваш новый пароль для входа: $password$</span><br /><br /><span>Если это письмо попало к Вам по ошибке просто проигнорируйте его.</span><br /><br /><span>При возникновении любых вопросов, обращайтесь по телефонам:</span><br /><br /><span>(012)&nbsp; 345-67-89 , (012)&nbsp; 345-67-89</span><br /><br /><span>---</span><br /><br /><span>С уважением,</span><br /><br /><span>сотрудники службы продаж $webSiteName$</span></p>', '', 'Шаблон письма на  восстановление пароля', 'a:5:{s:13:"$webSiteName$";s:17:"Имя сайта";s:18:"$resetPasswordUri$";s:59:"Ссылка на восстановления пароля";s:10:"$password$";s:12:"Пароль";s:5:"$key$";s:8:"Ключ";s:16:"$webMasterEmail$";s:54:"Email сотрудников службы продаж";}'),
(6, 'ru', 'Смена пароля', '<p><span>Здравствуйте $user_name$!</span><br /><br /><span>Ваш новый пароль для входа: $password$</span><br /><br /><span>Если это письмо попало к Вам по ошибке просто проигнорируйте его.</span><br /><br /><span><br /></span></p>', '', '<p>Шаблон письма изменения пароля</p>', 'a:2:{s:11:"$user_name$";s:31:"Имя пользователя";s:10:"$password$";s:23:"Новый пароль";}'),
(7, 'ru', 'Изменение цены', '<p>Цена на $name$ за которым вы следите на сайте $server$ изменилась.<br /> <a title="Посмотреть список слежения" href="$list_url_look$">Посмотреть список слежения</a><br /> <a title="Отписатся от слежения" href="$delete_list_url_look$">Отписатся от слежения</a></p>\n<div id="dc_vk_code"  none;">&nbsp;</div>', '<p>&nbsp;</p>\n<div id="dc_vk_code">&nbsp;</div>', '<p>Изменение цены</p>\n<div id="dc_vk_code" style="display: none;">&nbsp;</div>', ''),
(8, 'ru', 'Список Желаний', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Здравствуйте, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вы создали список желаний $wishName$</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Ссылка на просмотр списка желаний <a href="$wishLink$" target="_blank">$wishLink$</a></p>', '<p>Пользователь&nbsp;<span>$userName$ создал список желаний - $wishName$.<br /></span></p>\n<p><span><span>&nbsp;</span></span></p>', '<p><span>Уведомление о создании списка желаний</span></p>', 'a:4:{s:10:"$userName$";s:31:"Имя пользователя";s:10:"$wishName$";s:29:"Название списка";s:10:"$wishLink$";s:30:"Ссилка на список";s:15:"$wishListViews$";s:54:"Количество просмотров списка";}'),
(9, 'ru', 'Заказ звонка', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Здравствуйте, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вы заказали звонок в нашей компании<br />Менеджеры нашего магазина вскоре свяжутся с Вами.</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Вы указали следующие контактные данные:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Телефон: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Коментарий: </span>$userComment$</div>', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Новий запрос о Заказе дзвонка от $userName$</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Дата колбека: </span>$dateCreated$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Телефон пользователя: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Коментарий пользователя: </span>$userComment$</div>', '<p>Шаблон заказа звонка</p>', 'a:6:{s:16:"$callbackStatus$";s:27:"Статус колбека";s:15:"$callbackTheme$";s:23:"Тема колбека";s:10:"$userName$";s:69:"Имя пользователя запросившего звонок";s:11:"$userPhone$";s:90:"Номер телефона пользователя запросившего колбек";s:13:"$dateCreated$";s:23:"Дата колбека";s:13:"$userComment$";s:63:" Комментарии пользователя колбека";}'),
(1, 'ua', 'Замовлення товару', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Доброго дня, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Ми вдячні Вам за те, що Ви&nbsp;здійснили&nbsp;замовлення&nbsp;у нашому магазині.</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Ви вказали наступні&nbsp;контактні дані:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Email: </span>$userEmail$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Номер телефону: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Адреса доставки: </span>$userDeliver$</div>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Найближчим часом менеджери нашого магазину&nbsp;зв''яжуться з&nbsp;Вами та допоможуть&nbsp;з оформленням та оплатою товару.</p>\n<p style="font-family: arial; font-size: 13px;">Також, Ви завжди можете слідкувати за статусом Вашого замовлення, <a href="$orderLink$" target="_blank">перейшовши за посиланням</a>.</p>', '<p>Користувач&nbsp;$userName$&nbsp;здійснив замовлення товару</p>\n<p>Email: $userEmail$</p>\n<p>Номер телефону: $userPhone$</p>\n<p>Адреса доставки: $userDeliver$</p>', '<p><span>Повідомлення покупця про </span><span>здійснення</span><span>&nbsp;замовлення</span></p>', 'a:7:{s:10:"$userName$";s:30:"Ім''я користувача";s:11:"$userEmail$";s:28:"Email користувача";s:11:"$userPhone$";s:37:"Телефон користувача";s:13:"$userDeliver$";s:29:"Адреса доставки";s:11:"$orderLink$";s:44:"Посилання на замовлення";s:16:"$deliveryMethod$";s:29:"Спосіб доставки";s:15:"$paymentMethod$";s:25:"Спосіб оплати";}'),
(2, 'ua', 'Зміна статусу замовлення', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Статус вашого замовлення змінено на&nbsp;<strong>$status$</strong></p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Ви&nbsp;вказали наступні&nbsp;контактні дані:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Email: </span>$userEmail$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Номер телефону: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Адреса доставки: </span>$userDeliver$</div>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Найближчим часом з Вами зв''яжуться менеджери нашої компанії і допоможуть з оформленням та оплатою товару.</p>\n<p style="font-family: arial; font-size: 13px;">Також, Ви завжди можете слідкувати за статусом Вашого замовлення, <a href="$orderLink$" target="_blank">перейшовши&nbsp;за&nbsp;посиланням</a>.</p>', '', '<p>Зміна статусу замовлення</p>', 'a:6:{s:10:"$userName$";s:31:"Имя пользователя";s:11:"$userEmail$";s:30:"Email Пользователя";s:11:"$orderLink$";s:28:"Ссылка на заказ";s:8:"$status$";s:25:"Статус заказа";s:11:"$userPhone$";s:39:"Телефон пользователя";s:13:"$userDeliver$";s:27:"Адрес доставки";}'),
(3, 'ua', 'Товар з''явився на складі', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Товар <a href="$productLink$" target="_blank">$productName$</a> з''явився на складі. Ви можете його замовити.</p>', '', '', 'a:5:{s:10:"$userName$";s:31:"Имя пользователя";s:11:"$userEmail$";s:30:"Email Пользователя";s:13:"$productName$";s:33:"Название продукта";s:8:"$status$";s:12:"Статус";s:13:"$productLink$";s:32:"Ссылка на продукт";}'),
(4, 'ua', 'Створення нового користувача', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Доброго дня, $user_name$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо! Ваша реєстрація пройшла успішно.</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Дані для входу у магазин:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Email: </span>$user_email$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Пароль: </span>$user_password$</div>', '<p><span>Створений користувач&nbsp;$user_name$:</span><br /><span>З&nbsp;паролем: $user_password$</span><br /><span>Адресою: &nbsp;$<span>user_</span>address$</span><br /><span>Email користувача: $user_email$</span><br /><span>Телефон користувача: $user_phone$</span></p>', '<p><span>Шаблон листа на створення нового користувача</span></p>', 'a:5:{s:11:"$user_name$";s:31:"Имя пользователя";s:15:"$user_password$";s:12:"Пароль";s:14:"$user_address$";s:12:"Адресс";s:12:"$user_email$";s:5:"Email";s:12:"$user_phone$";s:14:"Телефон";}'),
(5, 'ua', 'Відновлення паролю', '<p><span>Доброго дня!</span><br /><br /><span>На сайті $webSiteName$ створений запит&nbsp;на відновлення&nbsp;паролю для Вашого акаунта.</span><br /><br /><span>Для завершення процедури відновлення&nbsp;паролю перейдіть по посиланню $resetPasswordUri$</span><br /><br /><span>Ваш новий пароль для входу: $password$</span><br /><br /><span>Якщо цей лист потрапив&nbsp;до Вас випадково,&nbsp;просто проігноруйте його.</span><br /><br /><span>При винекненні&nbsp;будь-яких запитань, звертайтесь&nbsp;за телефоном:</span><br /><br /><span>(000) &nbsp;000-00-00 , (000) &nbsp;000-00-00</span><br /><br /><span>---</span><br /><br /><span>З&nbsp;повагою,</span><br /><br /><span>Адміністрація сайту&nbsp;$webSiteName$</span></p>', '', '<p><span>Шаблон листа на відновлення паролю</span></p>', 'a:5:{s:13:"$webSiteName$";s:17:"Имя сайта";s:18:"$resetPasswordUri$";s:59:"Ссылка на восстановления пароля";s:10:"$password$";s:12:"Пароль";s:5:"$key$";s:8:"Ключ";s:16:"$webMasterEmail$";s:54:"Email сотрудников службы продаж";}'),
(6, 'ua', 'Зміна паролю', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо, $user_name$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Ви успішно змінили&nbsp;пароль</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Ваш новий пароль для входу: $password$</p>', '', '<p><span>Шаблон&nbsp;</span><span>листа на зміну паролю</span></p>', 'a:2:{s:11:"$user_name$";s:31:"Имя пользователя";s:10:"$password$";s:23:"Новый пароль";}'),
(7, 'ua', 'Ціна змінилася', '<p>Ціна на $name$ за яким Ви слідкуєте на сайті $server$ змінилася.<br /> <a title="Переглянути список слідкувань" href="$list_url_look$">Переглянути список слідкувань</a><br /> <a title="Відписатися від слідкування" href="$delete_list_url_look$">Відписатися від слідкування</a></p>\n<div id="dc_vk_code"  none;">&nbsp;</div>', '<p>&nbsp;</p>\n<div id="dc_vk_code">&nbsp;</div>', '<p>Слідкування за ціною</p>\n<div id="dc_vk_code" style="display: none;">&nbsp;</div>', ''),
(8, 'ua', 'Список бажань', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Ви створили&nbsp;список бажань $wishName$</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Посилання&nbsp;на перегляд&nbsp;списку бажань&nbsp;<a href="$wishLink$" target="_blank">$wishLink$</a></p>', '<p>Користувач&nbsp;<span>$userName$ створив список бажань&nbsp;- $wishName$.<br /></span></p>\n<p><span><span>&nbsp;</span></span></p>', '<p><span>Повідомлення&nbsp;про створення списку бажань</span></p>', 'a:4:{s:10:"$userName$";s:31:"Имя пользователя";s:10:"$wishName$";s:29:"Название списка";s:10:"$wishLink$";s:30:"Ссилка на список";s:15:"$wishListViews$";s:54:"Количество просмотров списка";}'),
(9, 'ua', 'Замовлення дзвінка', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Вітаємо, $userName$!</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Ви замовили дзвінок&nbsp;в нашій компанії<br />Найближчим часом менеджери нашого магазину свяжуться з Вами.</p>\n<p style="font-family: arial; font-size: 13px; margin-top: 20px;">Ви вказали наступні&nbsp;контактні дані:</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Телефон: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Коментар: </span>$userComment$</div>', '<p style="font-family: arial; font-size: 13px; margin-top: 10px;">Новий запит зворотнього дзвінка від $userName$</p>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Дата колбеку: </span>$dateCreated$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Телефон користувача: </span>$userPhone$</div>\n<div style="font-family: arial; font-size: 13px; margin-top: 10px;"><span style="color: #666;">Коментар користувача: </span>$userComment$</div>', '<p><span>Шаблон&nbsp;замовлення дзвінка</span></p>', 'a:4:{s:16:"$callbackStatus$";s:27:"Статус колбека";s:15:"$callbackTheme$";s:23:"Тема колбека";s:10:"$userName$";s:69:"Имя пользователя запросившего звонок";s:13:"$dateCreated$";s:23:"Дата колбека";}');